mapper.select:
select 

from ORDER_PAYMENT_DETAIL
where ORDER_PAYMENT_MAIN_ID = #{orderPaymentMainId,jdbcType=BIGINT}
  and PAYMENT_METHOD_CODE_ID = #{paymentMethodCodeId,jdbcType=BIGINT}
  and ORDER_PAYMENT_STATUS_CODE_ID = #{orderPaymentStatusCodeId,jdbcType=BIGINT}

mapper.delete:
delete from ORDER_PAYMENT_DETAIL
where ORDER_PAYMENT_MAIN_ID = #{orderPaymentMainId,jdbcType=BIGINT}
  and PAYMENT_METHOD_CODE_ID = #{paymentMethodCodeId,jdbcType=BIGINT}
  and ORDER_PAYMENT_STATUS_CODE_ID = #{orderPaymentStatusCodeId,jdbcType=BIGINT}

mapper.update:
update ORDER_PAYMENT_DETAIL
set APPROVAL_NUMBER = #{approvalNumber,jdbcType=VARCHAR},
  PAYMENT_AMOUNT = #{paymentAmount,jdbcType=INTEGER},
  PAYMENT_DATE = #{paymentDate,jdbcType=TIMESTAMP},
  PAYMENT_COMMENT = #{paymentComment,jdbcType=VARCHAR},
  CREATED_DATE = #{createdDate,jdbcType=TIMESTAMP},
  LAST_MODIFIED_DATE = #{lastModifiedDate,jdbcType=TIMESTAMP},
  LAST_MODIFIED_MEMBER = #{lastModifiedMember,jdbcType=BIGINT},
  DELETED_DATE = #{deletedDate,jdbcType=TIMESTAMP}
where ORDER_PAYMENT_MAIN_ID = #{orderPaymentMainId,jdbcType=BIGINT}
  and PAYMENT_METHOD_CODE_ID = #{paymentMethodCodeId,jdbcType=BIGINT}
  and ORDER_PAYMENT_STATUS_CODE_ID = #{orderPaymentStatusCodeId,jdbcType=BIGINT}
